import { Form, Input,Button } from "antd"
import { useCallback, useState, useEffect } from "react";
import useinput from "../hooks/useinput";
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from "react-redux";
import { ADD_COMMENT_REQUEST} from "../reducers/post";

const CommentForm = ({post}) =>{
    const dispatch = useDispatch();
    const id = useSelector((state)=> state.user.me?.id);
    const {addCommentDone, addPostDone} = useSelector((state) => state.post)
    const [commentText, onChangeCommentText, setCommentText] = useinput('');
    
    useEffect(() => {
        if(addCommentDone){
            setCommentText('');
        }
    }, [addPostDone])
    
    const onSubmitComment = useCallback(() => {    
        dispatch({
            type:ADD_COMMENT_REQUEST,
            data: {content: commentText, postId: post.id, userId: id },
        });
    },[commentText])
    return(
        <Form onFinish = {onSubmitComment}>
            <Form.Item style={{ position: 'relative', margin: 0}}>
                <Input.TextArea value={commentText} onChange = {onChangeCommentText} row={4} />
                <Button style={{ position:'absolute', right: 0, bottom: -40, zIndex: 1}} type="primary" htmlType="submit">삐약</Button>
            </Form.Item>
        </Form>
    )
}

CommentForm.propTypes = {
    post: PropTypes.object.isRequired,
};

export default CommentForm;
