import { all,fork, put, takeLatest, delay, call } from "@redux-saga/core/effects";
import axios from 'axios';
import { LOG_IN_SUCCESS, LOG_IN_REQUEST, LOG_IN_FAILURE,
         LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE,
         SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, 
         FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE,
         UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS, UNFOLLOW_FAILURE,
         LOAD_MY_INFO_FAILURE,LOAD_MY_INFO_REQUEST,LOAD_MY_INFO_SUCCESS, 
         CHANGE_NICKNAME_REQUEST,CHANGE_NICKNAME_SUCCESS,CHANGE_NICKNAME_FAILURE,
         LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWERS_SUCCESS, LOAD_FOLLOWINGS_FAILURE,
         LOAD_FOLLOWINGS_REQUEST, LOAD_FOLLOWINGS_SUCCESS, LOAD_FOLLOWERS_FAILURE,
         REMOVE_FOLLOWER_REQUEST, REMOVE_FOLLOWER_SUCCESS, REMOVE_FOLLOWER_FAILURE,
         }
from '../reducers/user'

function changeNicknameAPI(data) {
    return axios.patch('/user/nickname', {nickname: data });
};

function* changeNickname(action) {
    try{
        const result = yield call(changeNicknameAPI, action.data);
        yield put({
            type: CHANGE_NICKNAME_SUCCESS,
            data: result.data,
        });
    }catch (err){
        yield put({
            type: CHANGE_NICKNAME_FAILURE,
            error: err.response.data,   
        });
    }
};

function removeFollowerAPI(data) {
    return axios.delete(`/user/follower/${data}`,  data );
};

function* removeFollower(action) {
    try{
        const result = yield call(removeFollowerAPI, action.data);
        yield put({
            type: REMOVE_FOLLOWER_SUCCESS,
            data: result.data,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: REMOVE_FOLLOWER_FAILURE,
            error: err.response.data,   
        });
    }
};


function loadFollowersAPI(data) {
    return axios.get('/user/followers', data );
};

function* loadFollowers(action) {
    try{
        const result = yield call(loadFollowersAPI, action.data);
        yield put({
            type: LOAD_FOLLOWERS_SUCCESS,
            data: result.data,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOAD_FOLLOWERS_FAILURE,
            error: err.response.data,   
        });
    }
};

function loadFollowingsAPI(data) {
    return axios.get('/user/folowings',  data );
};

function* loadFollowings(action) {
    try{
        const result = yield call(loadFollowingsAPI, action.data);
        yield put({
            type: LOAD_FOLLOWINGS_SUCCESS,
            data: result.data,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOAD_FOLLOWINGS_FAILURE,
            error: err.response.data,   
        });
    }
};

function loadMyInfoAPI() {
    return axios.get('/user');
};

function* loadMyInfo(action) {
    try{
        const result = yield call(loadMyInfoAPI, action.data);
        yield put({
            type: LOAD_MY_INFO_SUCCESS,
            data: result.data,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOAD_MY_INFO_FAILURE,
            error: err.response.data,   
        });
    }
};

function loadUserAPI(data) {
    return axios.get(`/user/${data}`);
};

function* loadUser(action) {
    try{
        const result = yield call(loadUserAPI, action.data);
        yield put({
            type: LOAD_USER_SUCCESS,
            data: result.data,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOAD_USER_FAILURE,
            error: err.response.data,   
        });
    }
};


function logInAPI(data) {
    return axios.post('/user/login', data);
};

function* login(action) {
    try{
        const result = yield call(logInAPI, action.data);
        yield put({
            type: LOG_IN_SUCCESS,
            data: result.data,
        });
    }catch (err){
        yield put({
            type: LOG_IN_FAILURE,
            error: err.response.data,   
        });
    }
};

function logOutAPI() {
    return axios.post('/user/logout');
};

function* logOut() { 
    try{
        yield call(logOutAPI);
        yield put({
            type: LOG_OUT_SUCCESS,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOG_OUT_FAILURE,
            error: err.response.data,   
        });
    }
}

function signUpAPI(data) {
    return axios.post('/user', data);
}

function* signUp(action) {
    try{
        const result = yield call(signUpAPI, action.data);
        console.log(result);
        yield put({
            type: SIGN_UP_SUCCESS,
        });
    }catch (err){
        yield put({
            type: SIGN_UP_FAILURE,
            error: err.response.data,   
        })
    }
}

function followAPI(data) {
    return axios.patch(`/user/${data}/follow`);
}

function* follow(action) {
    try{
        const result = yield call(followAPI, action.data);
