import { all,fork, put, takeLatest, delay } from "@redux-saga/core/effects";
import axios from 'axios';
import { LOG_IN_SUCCESS, LOG_IN_REQUEST, LOG_IN_FAILURE,
         LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE,
         SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, 
         FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE,
         UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS, UNFOLLOW_FAILURE,}
from '../reducers/user'

function loginAPI(data) {
    return axios.post('/api.login', data);
}

function* login(action) {
    try{
        //const result = yield call(loginAPI, action.data);
        yield delay(1000);
        yield put({
            type: LOG_IN_SUCCESS,
            error: action.data,
        });
    }catch (err){
        yield put({
            type: LOG_IN_FAILURE,
            error: err.response.data,   
        })
    }
}

function logOutAPI() {
    return axios.post('/api.logOut');
}

function* logOut() {
    try{
        // const result = yield call(logOutAPI);
        yield delay(1000);
        yield put({
            type: LOG_OUT_SUCCESS,
            //data: action.data,
        });
    }catch (err){
        yield put({
            type: LOG_OUT_FAILURE,
            error: err.response.data,   
        })
    }
}

function signUpAPI() {
    return axios.post('/api.signUp');
}

function* signUp() {
    try{
        // const result = yield call(signUpAPI);
        yield delay(1000);
        yield put({
            type: SIGN_UP_SUCCESS,
            //data: action.data,
        });
    }catch (err){
        yield put({
            type: SIGN_UP_FAILURE,
            error: err.response.data,   
        })
    }
}


function* watchLogin() {
    yield takeLatest(LOG_IN_REQUEST, login); //로그인이 실행될때까지 기다리겠다는 뜻
}
function* watchLogOut() {
    yield takeLatest(LOG_OUT_REQUEST,logOut);
}
function* watchSignUp () {
    yield takeLatest(SIGN_UP_REQUEST, signUp);
}
function* watchFollow() {
    yield takeLatest(FOLLOW_REQUEST, followUp)
}
function* watchUnfollow() {
    yield takeLatest(UNFOLLOW_REQUEST, unfollowUp)
}
    
export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchSignUp),
        fork(watchUnfollow),
        fork(watchFollow),

    ])
}
