export const initalState = {
    mainPosts: [{
        id: 1,
        User:{
            id: 1,
            nickname:'제로초',
        },
        content: '첫번째 게시물 입니다. #해시태그 #익스프레스',
        Images : [{
            src: 'https://cdn.mkhealth.co.kr/news/photo/202010/50970_51164_4758.jpg'
        },{
            src: 'https://cdn.mkhealth.co.kr/news/photo/202010/50970_51164_4758.jpg'
        },{
            src : 'https://cdn.mkhealth.co.kr/news/photo/202010/50970_51164_4758.jpg'
        }],
        Comments : [{
            User:{
                nickname: 'nero',
            },
            content: '우와 개정판이 나왔군요',
        }, {
            User : {
                nickname : 'hero',
            },
            content : '우아 ㅇ우ㅏ',
        }]
    }],
    imagePaths: [],
    pastAdded: false,
}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type:'ADD_POST',
}

const dummyPost = {
    id:2,
    content: '덤데이터 입니다',
    User: {
        id: 1,
        nickname: '젤초',
    },
    images: [],
    Comments:[],
};



const reducer = (state = initalState, action) => {
    switch (action.type) {
        case ADD_POST:
            return{
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                postAdded: true,
            };
        default:
            return state;
    }
};

export default reducer;
