const initalState = {
    user: {
        isLoggedIn: false,
        user : null,
        signUpData: {},
        loginData: {},
    },
    post : {
        mainPosts: [],

    }
};

export const loginAction = (data) => {
    return{
        type:"LOG_IN",

    }
}
export const logoutAction = (data) => {
    return{
        type:"LOG_OUT",

    }
}

const rootReducer = (state = initalState, action)=>{
    switch(action.type) {
        case 'LOG_IN':
            return{
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                },
            };
        case 'LOG_OUT':
            return{
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null,
                },
            };
    }
};

export default rootReducer
