import { all,fork, put, takeLatest, delay, call } from "@redux-saga/core/effects";
import axios from 'axios';
import { LOG_IN_SUCCESS, LOG_IN_REQUEST, LOG_IN_FAILURE,
         LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE,
         SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, 
         FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE,
         UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS, UNFOLLOW_FAILURE,
         LOAD_MY_INFO_FAILURE,LOAD_MY_INFO_REQUEST,LOAD_MY_INFO_SUCCESS, CHANGE_NICKNAME_REQUEST,}
from '../reducers/user'

function loadMyInfoAPI() {
    return axios.get('/user');
};

function* loadMyInfo(action) {
    try{
        const result = yield call(loadMyInfoAPI, action.data);
        yield put({
            type: LOAD_MY_INFO_SUCCESS,
            data: result.data,
        });
    }catch (err){
        yield put({
            type: LOAD_MY_INFO_FAILURE,
            error: err.response.data,   
        });
    }
};


function logInAPI(data) {
    return axios.post('/user/login', data);
};

function* login(action) {
    try{
        const result = yield call(logInAPI, action.data);
        yield put({
            type: LOG_IN_SUCCESS,
            data: result.data,
        });
    }catch (err){
        yield put({
            type: LOG_IN_FAILURE,
            error: err.response.data,   
        });
    }
};

function logOutAPI() {
    return axios.post('/user/logout');
};

function* logOut() { 
    try{
        yield call(logOutAPI);
        yield put({
            type: LOG_OUT_SUCCESS,
        });
    }catch (err){
        console.error(err);
        yield put({
            type: LOG_OUT_FAILURE,
            error: err.response.data,   
        });
    }
}

function signUpAPI(data) {
    return axios.post('/user', data);
}

function* signUp(action) {
    try{
        const result = yield call(signUpAPI, action.data);
        console.log(result);
        yield put({
            type: SIGN_UP_SUCCESS,
        });
    }catch (err){
        yield put({
            type: SIGN_UP_FAILURE,
            error: err.response.data,   
        })
    }
}

function followAPI() {
    return axios.post('/api.follow');
}

function* follow(action) {
    try{
        // const result = yield call(signUpAPI);
        yield delay(1000);
        yield put({
            type: FOLLOW_SUCCESS,
            data: action.data,
        });
    }catch (err){
        yield put({
            type: FOLLOW_FAILURE,
            error: err.response.data,   
        })
    }
}

function unfollowAPI() {
    return axios.post('/api.unfollow');
}

function* unfollow(action) {
    try{
        // const result = yield call(signUpAPI);
        yield delay(1000);
        yield put({
            type: UNFOLLOW_SUCCESS,
            data: action.data,
        });
    }catch (err){
        yield put({
            type: UNFOLLOW_FAILURE,
            error: err.response.data,   
        })
    }
}



function* watchLogin() {
    yield takeLatest(LOG_IN_REQUEST, login); //로그인이 실행될때까지 기다리겠다는 뜻
}
function* watchLogOut() {
    yield takeLatest(LOG_OUT_REQUEST,logOut);
}
function* watchChangeNickname() {
    yield takeLatest(CHANGE_NICKNAME_REQUEST,changeNickname);
}
function* watchSignUp () {
    yield takeLatest(SIGN_UP_REQUEST, signUp);
}
function* watchFollow() {
    yield takeLatest(FOLLOW_REQUEST, follow);
}
function* watchUnfollow() {
    yield takeLatest(UNFOLLOW_REQUEST, unfollow);
}
function* watchLoadMyInfo() {
    yield takeLatest(LOAD_MY_INFO_REQUEST, loadMyInfo);
}
    
export default function* userSaga() {
    yield all([
        fork(watchLoadMyInfo),
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchSignUp),
        fork(watchUnfollow),
        fork(watchFollow),
        fork(watchChangeNickname),
    ])
}
