export const initalState = {
    isLoggedIn: false,
    me : null,
    signUpData: {},
    loginData: {},
}

export const loginRequestAction = (data) => {
    return{
        type:"LOG_IN_REQUEST",
        data,
    }
}
export const loginSuccessAction = (data) => {
    return{
        type:"LOG_IN_SUCCESS",
        data,
    }
}
export const loginFailureAction = (data) => {
    return{
        type:"LOG_IN_FAILURE",
        data,
    }
}
export const logoutRequsetAction = (data) => {
    return{
        type:"LOG_OUT_REQUEST",
        data,
    }
}
export const logoutSuccessAction = (data) => {
    return{
        type:"LOG_OUT-SUCCESS",
        data,
    }
}
export const logoutFailureAction = (data) => {
    return{
        type:"LOG_OUT-FAILURE",
        data,
    }
}

const reducer = (state = initalState, action) => {
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            return{
                    ...state,
                    isLoggedIn: true,
                    me: action.data,
            };
        case 'LOG_IN_SUCCESS':
            return{
                    ...state,
                    isLoggedIn: true,
                    me: action.data,
            };
        case 'LOG_IN_FAILURE':
            return{
                    ...state,
                    isLoggedIn: true,
                    me: action.data,
            };
        case 'LOG_OUT_REQUEST':
            return{
                    ...state,
                    isLoggedIn: false,
                    me: null,
            };
        case 'LOG_OUT_SUCCESS':
            return{
                    ...state,
                    isLoggedIn: false,
                    me: null,
            };
        case 'LOG_OUT_FAILURE':
            return{
                    ...state,
                    isLoggedIn: false,
                    me: null,
            };
        default:
            return state;
    }
};

export default reducer;
