import React, { useCallback, useState } from 'react';
import AppLayout from '../componets/AppLayout';
import Head from 'next/head';
import { Button, Checkbox, Form, Input } from 'antd';
import useinput from '../hooks/useinput';
import styled from 'styled-components';

const ErrorMessage = styled.div`
    color:red;
`;



const SignUp = () => {

   const [id, onChangeId] = useinput('');
   const [nickname, onChangeNickname] = useinput('');
   const [pw, onChangePw] = useinput('');

   const [pwCheck, setPwCheck] = useState('');
   const [pwError, setPwError] = useState(false); 
   const onChangePwCheck = useCallback((e)=>{
        setPwCheck(e.target.value);
        setPwError(e.target.value !== pw);
   },[pw]);
   const [term, setTerm] = useState('');
   const [termError, setTermError] = useState(false); 
   const onChangeTerm = useCallback((e)=>{
        setTerm(e.target.checked);
        setTermError(false);
   },[]);

   const onSubmit = useCallback(()=>{
    if(pw !== pwCheck){
        return setPwError(true)
    };
    if(!term){
        return setTermError(true)
    };
    console.log(id, nickname, pw);
    },[pw, pwCheck, term]);

    return (
        <AppLayout>
        <Head>
            <title>Node Bird | 회원가입</title>
        </Head>
            <Form onFinish = {onSubmit}>
                <div>
                    <label htmlFor='user-id'>아이디</label>
                    <br />
                    <Input name="user-id" value={ id } required onChange={onChangeId} />
                </div>
                <div>
                    <label htmlFor='user-nick'>닉네임</label>
                    <br />
                    <Input name="user-nick" value={ nickname } required onChange={onChangeNickname} />
                </div>
                <div>
                    <label htmlFor='user-pw'>비밀번호</label>
                    <br />
                    <Input name="user-pw" type="password" value={ pw } required onChange={onChangePw} />
                </div>
                <div>
                    <label htmlFor="user-pw-check">비밀번호 체크</label>
                    <br />
                    <Input
                        name="user-pw-check"
                        type="password"
                        value={ pwCheck }
                        required
                        onChange = {onChangePwCheck } />
                        {pwError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
                </div>
                <div>
                    <Checkbox name="user-term" checked = {term} onChange = {onChangeTerm}>동의합니다</Checkbox>
                    {termError && <ErrorMessage>약관에 동의하셔야 합니다</ErrorMessage>}
                </div>
                <div style={{marginTop:10}}>
                        <Button type="primary" htmlType="submit">가입하기</Button>
                </div>
            </Form>
        </AppLayout>
    )
}


export default SignUp;
