export const initalState = {
    isLoggedIn: false,
    me : null,
    signUpData: {},
    loginData: {},
}

export const loginAction = (data) => {
    return{
        type:"LOG_IN",
        data,
    }
}
export const logoutAction = (data) => {
    return{
        type:"LOG_OUT",
        data,
    }
}

const reducer = (state = initalState, action) => {
    switch (action.type) {
        case 'LOG_IN':
            return{
                    ...state,
                    isLoggedIn: true,
                    me: action.data,
            };
        case 'LOG_OUT':
            return{
                    ...state,
                    isLoggedIn: false,
                    me: null,
            };
        default:
            return state;
    }
};

export default reducer;
